import React, { useState } from "react";
import styles from "../test/nameFrom.module.css"

import cats from "../../data/cats.js"

function getRandomInt(max) {
    return Math.floor(Math.random() * max);
  }


export default function NameForm(props) {
  const [name, setName] = useState("");
  const [notes, setNotes] = useState("");
  const [catPic, setPic] = useState("");
  const [catState, setCats] = useState(cats)

const randomInt = getRandomInt(300)

useEffect(() => {
    NameForm();
  }, []);

  const handleSubmit = (evt) => {
      evt.preventDefault();
      //alert(`Submitting Name ${name}, ${notes},${catPic}`)
      cats.push(  {
        key: `${randomInt}`,
        name: `${name}`,
        img: `${catPic}`,
        notes: `${notes}`,
      })

  }
  return (
    <form className={styles.nameForm} onSubmit={handleSubmit}>
      <label>
        Cat Name:
        <input
          type="text"
          value={name}
          onChange={e => setName(e.target.value)}
        />
      </label>


      <label>
        Cat Notes:
        <input
          type="text"
          value={notes}
          onChange={f => setNotes(f.target.value)}
        />
      </label>


      <label>
        Cat Pic:
        <input
          type="text"
          value={catPic}
          onChange={g => setPic(g.target.value)}
        />
      </label>


      <input className={styles.button} type="submit" value="Submit" />
    </form>
  );
}
